import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';

const App = () =>{
    const [numb, setN] = useState(0);

    const increment = () =>{
        setN(numb+1);
    }

    const decrement = () =>{
        if((numb-1)<0){
            alert("You cant go below 0 ðŸ™‚");
        }
        else{
            setN(numb-1);
        }
    }

    return(
        <>
            <div className="body">
                <div className="main">
                    <h1> {numb} </h1>
                    <div className="incdec">
                        <button onClick = {increment}> <AddIcon /> </button>
                        <button onClick = {decrement}> Decre</button>
                    </div>
                </div>
            </div>
        </>
    );
};

export default App;
